import React, { Component } from 'react';
import './App.css';
import { withScriptjs, withGoogleMap, GoogleMap, Marker } from "react-google-maps"

class App extends Component {
  state = {
    data: '',
    district: '',
    carpark: '',
  };

  async componentDidMount() {
    const response = await fetch('/api/data', {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
      }
    });

    const body = await response.json();

    this.setState({ data: body });
  }

  createDistrictOptions() {
    let districtList = [];

    for (var i in this.state.data) {
      districtList.push(<option key={i} value={this.state.data[i].Ilce}>{this.state.data[i].Ilce}</option>)
    }

    districtList.filter((dist, i) => districtList.indexOf(dist) === i);

    console.log(districtList);

    return districtList;
  }

  createCarParkOptions() {
    let carParkList = [];

    for (var i in this.state.data) {
      if (this.state.data[i].Ilce === this.state.district)
        carParkList.push(<option key={i} value={this.state.data[i].ParkAdi}>{this.state.data[i].ParkAdi}</option>)
    }

    return carParkList;
  }

  getFreeParkingSpaces() {
    let capacity = 0;

    for (var i in this.state.data) {

      if (this.state.data[i].ParkAdi === this.state.carpark)
        capacity = this.state.data[i].BosKapasite;
    }

    return capacity;
  }

  getUsedParkingSpaces() {
    let capacity = 0;
    let freeCapacity = 0;

    for (var i in this.state.data) {

      if (this.state.data[i].ParkAdi === this.state.carpark) {
        freeCapacity = this.state.data[i].BosKapasite;
        capacity = this.state.data[i].Kapasitesi;
      }
    }

    return capacity - freeCapacity;
  }

  getLatitude() {
    let lat = 40.96;

    for (var i in this.state.data) {

      if (this.state.data[i].ParkAdi === this.state.carpark) {
        lat = this.state.data[i].Latitude;
      }
    }

    return lat;
  }

  getLongitude() {
    let lon = 29.10;

    for (var i in this.state.data) {

      if (this.state.data[i].ParkAdi === this.state.carpark) {
        lon = this.state.data[i].Longitude;
      }
    }

    return lon;

  }

  render() {
    const CarParkLocation = withScriptjs(withGoogleMap((props) =>
      <GoogleMap
        defaultZoom={ 15 }
        defaultCenter={{ lat: props.lat, lng: props.lon }}
      >
        { props.showMarker && 
          <Marker position={{ lat: props.lat, lng: props.lon }} />
        }
      </GoogleMap>
    ))

    const DistrictSelectionForm = () =>
        <form>
            <p>
              <strong>Districts of Car Parks:</strong>
            </p>
            <select
              value={this.state.district}
              onChange={e => this.setState({ district: e.target.value })}>
                  {this.createDistrictOptions()}
            </select>

        </form>

    const CarParkSelectionForm = () => 
        <form>
            <p>
              <strong>Car Parks:</strong>
            </p>
            <select
              value={this.state.carpark}
              onChange={e => this.setState({ carpark: e.target.value })}>
                  {this.createCarParkOptions()}
            </select>

        </form>

    return (
      <div className="App">
        <header className="App-header">
          Ispark Car Parks
        </header>

        <DistrictSelectionForm/>

        <CarParkSelectionForm/>

        <p>
          <strong>Free Parking Spaces:</strong>
        </p>

        {this.getFreeParkingSpaces()}

        <CarParkLocation
          showMarker="true"
          lon={Number(this.getLongitude())}
          lat={Number(this.getLatitude())}
          googleMapURL="https://maps.googleapis.com/maps/api/js?v=3.exp&libraries=geometry,drawing,places"
          loadingElement={<div style={{ height: '100%' }} />}
          containerElement={<div style={{ height: '500px' }} />}
          mapElement={<div style={{ height: '100%' }} />}
        />

      </div>
    );
  }
}

export default App;
