import React, { Component } from 'react';
import './App.css';
import { withScriptjs, withGoogleMap, GoogleMap, Marker } from "react-google-maps"

class App extends Component {
  state = {
    data: '',
    district: '',
    carpark: '',
    districtResponse: '',
    carparkResponse: '',
    testDistricts: ''
  };

  async componentDidMount() {
    const url = "https://api.ibb.gov.tr/ispark/Park";
    const response = await fetch(url);

    const data = await response.json();

    this.setState({ data: data} )
  }

  handleDistrictSelection = async e => {

    e.preventDefault();

    const response = await fetch('/district', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ post: this.state.district }),
    });

    const body = await response.text();

    this.setState({ districtResponse: body });

  };

  handleCarParkSelection = async e => {

    e.preventDefault();

    const response = await fetch('/carpark', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ post: this.state.carpark })
    });

    const body = await response.text();

    this.setState({ carparkResponse: body });

  }

  getDistricts = async e => {

    e.preventDefault();

    console.log("get districts")

    const response = await fetch('/api/districts', {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
      }
    });

    const body = await response.json();

    this.setState({ testDistricts: body });
  }

  createDistrictOptions() {
    let districtList = [];

    this.getDistricts();

    for (var i in this.state.testDistricts) {
      districtList.push(<option key={i} value={this.state.testDistricts[i]}>{this.state.data[i]}</option>)
    }

    return districtList;
  }

  createCarParkOptions() {
    let carParkList = [];

    for (var i in this.state.data) {
      if (this.state.data[i].Ilce === this.state.district)
        carParkList.push(<option key={i} value={this.state.data[i].ParkAdi}>{this.state.data[i].ParkAdi}</option>)
    }

    return carParkList;
  }

  getFreeParkingSpaces() {
    let capacity = 0;

    for (var i in this.state.data) {

      if (this.state.data[i].ParkAdi === this.state.carpark)
        capacity = this.state.data[i].BosKapasite;
    }

    return capacity;
  }

  getUsedParkingSpaces() {
    let capacity = 0;
    let freeCapacity = 0;

    for (var i in this.state.data) {

      if (this.state.data[i].ParkAdi === this.state.carpark) {
        freeCapacity = this.state.data[i].BosKapasite;
        capacity = this.state.data[i].Kapasitesi;
      }
    }

    return capacity - freeCapacity;
  }

  getLatitude() {
    let lat = 40.96;

    for (var i in this.state.data) {

      if (this.state.data[i].ParkAdi === this.state.carpark) {
        lat = this.state.data[i].Latitude;
      }
    }

    return lat;
  }

  getLongitude() {
    let lon = 29.10;

    for (var i in this.state.data) {

      if (this.state.data[i].ParkAdi === this.state.carpark) {
        lon = this.state.data[i].Longitude;
      }
    }

    return lon;

  }

  render() {
    const CarParkLocation = withScriptjs(withGoogleMap((props) =>
      <GoogleMap
        defaultZoom={ 15 }
        defaultCenter={{ lat: props.lat, lng: props.lon }}
      >
        { props.showMarker && 
          <Marker position={{ lat: props.lat, lng: props.lon }} />
        }
      </GoogleMap>
    ))

    const DistrictSelectionForm = () =>
        <form>
            <p>
              <strong>Districts of Car Parks:</strong>
            </p>
            <select
              value={this.state.district}
              onChange={e => this.setState({ district: e.target.value })}>
                  {this.createDistrictOptions()}
            </select>

        </form>

    const CarParkSelectionForm = () => 
        <form>
            <p>
              <strong>Car Parks:</strong>
            </p>
            <select
              value={this.state.carpark}
              onChange={e => this.setState({ carpark: e.target.value })}>
                  {this.createCarParkOptions()}
            </select>

        </form>

    return (
      <div className="App">
        <header className="App-header">
          Ispark Car Parks
        </header>

        <DistrictSelectionForm/>

        <p>{this.state.districtResponse}</p>

        <CarParkSelectionForm/>

        <p>{this.state.carparkResponse}</p>

        <p>
          <strong>Free Parking Spaces:</strong>
        </p>

        {this.getFreeParkingSpaces()}

        <CarParkLocation
          showMarker="true"
          lon={Number(this.getLongitude())}
          lat={Number(this.getLatitude())}
          googleMapURL="https://maps.googleapis.com/maps/api/js?v=3.exp&libraries=geometry,drawing,places"
          loadingElement={<div style={{ height: '100%' }} />}
          containerElement={<div style={{ height: '500px' }} />}
          mapElement={<div style={{ height: '100%' }} />}
        />

      </div>
    );
  }
}

export default App;
